import { useEffect, useState } from 'react'
import { observer } from 'mobx-react-lite'
import { useRootStore } from '../../stores/root.store'
import {
  Alert, Box, Button, Card, CardContent, CardHeader,
  Divider, IconButton, Stack, TextField, Typography, Tooltip
} from '@mui/material'
import DeleteForeverIcon from '@mui/icons-material/DeleteForever'
import type { Post } from '../../types'

type Props = { post: Post }

const PostCard = observer(({ post }: Props) => {
  const { posts, auth } = useRootStore()
  const [showComments, setShowComments] = useState(false)
  const [commentText, setCommentText] = useState('')
  const [err, setErr] = useState<string | null>(null)

  const selfPost = auth.user?.id === post.authorId
  const canDeletePost = selfPost || auth.canModerate

  useEffect(() => {
    if (showComments && posts.comments(post.id).length === 0) {
      posts.fetchComments(post.id)
    }
  }, [showComments])

  const onAddComment = async () => {
    if (!commentText.trim()) return
    try {
      await posts.addComment(post.id, commentText.trim())
      setCommentText('')
    } catch (e: any) {
      setErr(e?.response?.data?.message || e?.message || 'Не удалось добавить комментарий')
    }
  }

  const comments = posts.comments(post.id)

  return (
    <Card>
      <CardHeader
        title={<Typography variant="subtitle1">{post.authorName}</Typography>}
        subheader={new Date(post.createdAt).toLocaleString()}
        action={canDeletePost && (
          <Tooltip title={selfPost ? 'Удалить мой пост' : 'Удалить пост'}>
            <span>
              <IconButton
                size="small"
                color="error"
                disabled={posts.isPostProcessing(post.id)}
                onClick={() => posts.hardDeletePost(post.id)}
              >
                <DeleteForeverIcon />
              </IconButton>
            </span>
          </Tooltip>
        )}
      />
      <CardContent>
        {post.imageUrl && (
          <img src={post.imageUrl} alt="" style={{ maxWidth: '100%', borderRadius: 12, marginBottom: 12 }} />
        )}
        <Typography>{post.description}</Typography>

        <Stack direction="row" spacing={1} mt={1} alignItems="center">
          <Button size="small" onClick={() => setShowComments(s => !s)}>
            Комментарии ({post.commentsCount ?? comments.length})
          </Button>
        </Stack>

        {showComments && (
          <Box mt={2}>
            <Divider sx={{ mb: 2 }} />
            {comments.map(c => {
              const selfComment = auth.user?.id === c.authorId
              const canDeleteComment = selfComment || auth.canModerate
              return (
                <Box key={c.id} sx={{ mb: 1.5 }}>
                  <Stack direction="row" justifyContent="space-between" alignItems="center">
                    <Typography variant="subtitle2">
                      {c.authorName} · <span style={{ fontWeight: 400, opacity: .8 }}>{new Date(c.createdAt).toLocaleString()}</span>
                    </Typography>
                    {canDeleteComment && (
                      <Tooltip title={selfComment ? 'Удалить мой комментарий' : 'Удалить комментарий'}>
                        <span>
                          <IconButton
                            size="small"
                            color="error"
                            disabled={posts.isCommentProcessing(c.id)}
                            onClick={() => posts.hardDeleteComment(post.id, c.id)}
                          >
                            <DeleteForeverIcon fontSize="small" />
                          </IconButton>
                        </span>
                      </Tooltip>
                    )}
                  </Stack>
                  <Typography>{c.text}</Typography>
                </Box>
              )
            })}   

            {auth.isAuthenticated && !auth.user?.isBlocked && (
              <Stack direction="row" spacing={1} mt={2}>
                <TextField
                  size="small"
                  placeholder="Написать комментарий..."
                  value={commentText}
                  onChange={(e) => setCommentText(e.target.value)}
                  fullWidth
                />
                <Button variant="contained" onClick={onAddComment} disabled={!commentText.trim()}>
                  Отправить
                </Button>
              </Stack>
            )}

            {err && <Alert severity="error" sx={{ mt: 1 }}>{err}</Alert>}
          </Box>
        )}
      </CardContent>
    </Card>
  )
})

export default PostCard
