import { makeAutoObservable, runInAction } from 'mobx'
import http from '../api/http'
import type { CredentialsDTO, RegisterDTO, User } from '../types'

export class AuthStore {
  user: User | null = null
  token: string | null = null
  isLoading = false
  initializing = true
  error: string | null = null

  constructor() {
    makeAutoObservable(this)
    this.hydrate()
  }

  get isAuthenticated() {
    return !!this.token && !!this.user
  }

  get isAdmin() {
    return this.user?.role === 'admin'
  }

    hydrate() {
    const token = localStorage.getItem('token')
    const userRaw = localStorage.getItem('user')

    this.token = token ?? null

    if (userRaw && userRaw !== 'undefined' && userRaw !== 'null') {
        try {
        this.user = JSON.parse(userRaw)
        } catch {
        localStorage.removeItem('user')
        this.user = null
        }
    } else {
        if (userRaw) localStorage.removeItem('user')
        this.user = null
    }

    this.initializing = false
    }


    async login(dto: CredentialsDTO) {
    this.isLoading = true
    this.error = null
    try {
        // Порядок важен: сначала /auth (как у тебя на бэке)
        const { data } = await postFallback<{ token: string; user: User }>(
        ['/auth', '/auth/login', '/login', '/api/auth/login'],
        dto
        )

        runInAction(() => {
        this.token = data.token
        this.user = data.user
        localStorage.setItem('token', data.token)
        localStorage.setItem('user', JSON.stringify(data.user))
        })
    } catch (e: any) {
        runInAction(() => {
        this.error = e?.response?.data?.message || 'Ошибка входа'
        })
        // пробросим, чтобы форма могла отреагировать
        throw e
    } finally {
        runInAction(() => (this.isLoading = false))
    }
    }

  async register(dto: RegisterDTO) {
    this.isLoading = true
    this.error = null
    try {
      const { data } = await http.post<{ token: string; user: User }>('/register', dto)
      runInAction(() => {
        this.token = data.token
        this.user = data.user
        localStorage.setItem('token', data.token)
        localStorage.setItem('user', JSON.stringify(data.user))
      })
    } catch (e: any) {
      runInAction(() => {
        this.error = e?.response?.data?.message || 'Ошибка регистрации'
      })
      throw e
    } finally {
      runInAction(() => (this.isLoading = false))
    }
  }

  logout() {
    this.token = null
    this.user = null
    localStorage.removeItem('token')
    localStorage.removeItem('user')
  }
}
